    31‚Üí
    32‚Üí# Theming
    33‚Üí: "${ZSH_THEME_GIT_PROMPT_PREFIX="["}"
    34‚Üí: "${ZSH_THEME_GIT_PROMPT_SUFFIX="] "}"
    35‚Üí: "${ZSH_THEME_GIT_PROMPT_SEPARATOR="|"}"
    36‚Üí: "${ZSH_THEME_GIT_PROMPT_DETACHED="%{$fg_bold[cyan]%}:"}"
    37‚Üí: "${ZSH_THEME_GIT_PROMPT_BRANCH="%{$fg_bold[magenta]%}"}"
    38‚Üí: "${ZSH_THEME_GIT_PROMPT_UPSTREAM_SYMBOL="%{$fg_bold[yellow]%}‚ü≥ "}"
    39‚Üí: "${ZSH_THEME_GIT_PROMPT_UPSTREAM_NO_TRACKING=""}"
    40‚Üí: "${ZSH_THEME_GIT_PROMPT_UPSTREAM_PREFIX="%{$fg[red]%}(%{$fg[yellow]%}"}"
    41‚Üí: "${ZSH_THEME_GIT_PROMPT_UPSTREAM_SUFFIX="%{$fg[red]%})"}"
    42‚Üí: "${ZSH_THEME_GIT_PROMPT_BEHIND="‚Üì"}"
    43‚Üí: "${ZSH_THEME_GIT_PROMPT_AHEAD="‚Üë"}"
    44‚Üí: "${ZSH_THEME_GIT_PROMPT_UNMERGED="%{$fg[red]%}‚úñ"}"
    45‚Üí: "${ZSH_THEME_GIT_PROMPT_STAGED="%{$fg[green]%}‚óè"}"
    46‚Üí: "${ZSH_THEME_GIT_PROMPT_UNSTAGED="%{$fg[red]%}‚úö"}"
    47‚Üí: "${ZSH_THEME_GIT_PROMPT_UNTRACKED="‚Ä¶"}"
    48‚Üí: "${ZSH_THEME_GIT_PROMPT_STASHED="%{$fg[blue]%}‚öë"}"
    49‚Üí: "${ZSH_THEME_GIT_PROMPT_CLEAN="%{$fg_bold[green]%}‚úî"}"
    50‚Üí: "${ZSH_THEME_GIT_PROMPT_SECONDARY_PREFIX=""}"
    51‚Üí: "${ZSH_THEME_GIT_PROMPT_SECONDARY_SUFFIX=""}"
    52‚Üí: "${ZSH_THEME_GIT_PROMPT_TAGS_SEPARATOR=", "}"
    53‚Üí: "${ZSH_THEME_GIT_PROMPT_TAGS_PREFIX="üè∑ "}"
    54‚Üí: "${ZSH_THEME_GIT_PROMPT_TAGS_SUFFIX=""}"
    55‚Üí: "${ZSH_THEME_GIT_PROMPT_TAG="%{$fg_bold[magenta]%}"}"
    56‚Üí
    57‚Üí# Disable promptinit if it is loaded
    58‚Üí(( $+functions[promptinit] )) && {promptinit; prompt off}
    59‚Üí
    60‚Üí# Allow parameter and command substitution in the prompt
    61‚Üísetopt PROMPT_SUBST
    62‚Üí
    63‚Üí# Override PROMPT if it does not use the gitprompt function
    64‚Üí[[ "$PROMPT" != *gitprompt* && "$RPROMPT" != *gitprompt* ]] \
    65‚Üí    && PROMPT='%B%40<..<%~ %b$(gitprompt)' \
    66‚Üí    && PROMPT+='%(?.%(!.%F{white}‚ùØ%F{yellow}‚ùØ%F{red}.%F{blue}‚ùØ%F{cyan}‚ùØ%F{green})‚ùØ.%F{red}‚ùØ‚ùØ‚ùØ)%f '
    67‚Üí
    68‚Üí# Find an awk implementation
    69‚Üí# Prefer nawk over mawk and mawk over awk
    70‚Üí(( $+commands[mawk] ))  &&  : "${ZSH_GIT_PROMPT_AWK_CMD:=mawk}"
    71‚Üí(( $+commands[nawk] ))  &&  : "${ZSH_GIT_PROMPT_AWK_CMD:=nawk}"
