    74‚Üífunction _zsh_git_prompt_git_status() {
    75‚Üí    emulate -L zsh
    76‚Üí    {
    77‚Üí        [[ -n "$ZSH_GIT_PROMPT_SHOW_STASH" ]] && (
    78‚Üí            c=$(command git rev-list --walk-reflogs --count refs/stash 2> /dev/null)
    79‚Üí            [[ -n "$c" ]] && echo "# stash.count $c"
    80‚Üí        )
    81‚Üí        GIT_OPTIONAL_LOCKS=0 command git status --branch --porcelain=v2 2>&1 \
    82‚Üí            || echo "fatal: git command failed"
    83‚Üí    } | $ZSH_GIT_PROMPT_AWK_CMD \
    84‚Üí        -v PREFIX="$ZSH_THEME_GIT_PROMPT_PREFIX" \
    85‚Üí        -v SUFFIX="$ZSH_THEME_GIT_PROMPT_SUFFIX" \
    86‚Üí        -v SEPARATOR="$ZSH_THEME_GIT_PROMPT_SEPARATOR" \
    87‚Üí        -v DETACHED="$ZSH_THEME_GIT_PROMPT_DETACHED" \
    88‚Üí        -v BRANCH="$ZSH_THEME_GIT_PROMPT_BRANCH" \
    89‚Üí        -v UPSTREAM_TYPE="$ZSH_GIT_PROMPT_SHOW_UPSTREAM" \
    90‚Üí        -v UPSTREAM_SYMBOL="$ZSH_THEME_GIT_PROMPT_UPSTREAM_SYMBOL" \
    91‚Üí        -v UPSTREAM_NO_TRACKING="$ZSH_THEME_GIT_PROMPT_UPSTREAM_NO_TRACKING" \
    92‚Üí        -v UPSTREAM_PREFIX="$ZSH_THEME_GIT_PROMPT_UPSTREAM_PREFIX" \
    93‚Üí        -v UPSTREAM_SUFFIX="$ZSH_THEME_GIT_PROMPT_UPSTREAM_SUFFIX" \
    94‚Üí        -v BEHIND="$ZSH_THEME_GIT_PROMPT_BEHIND" \
    95‚Üí        -v AHEAD="$ZSH_THEME_GIT_PROMPT_AHEAD" \
    96‚Üí        -v UNMERGED="$ZSH_THEME_GIT_PROMPT_UNMERGED" \
    97‚Üí        -v STAGED="$ZSH_THEME_GIT_PROMPT_STAGED" \
    98‚Üí        -v UNSTAGED="$ZSH_THEME_GIT_PROMPT_UNSTAGED" \
    99‚Üí        -v UNTRACKED="$ZSH_THEME_GIT_PROMPT_UNTRACKED" \
   100‚Üí        -v STASHED="$ZSH_THEME_GIT_PROMPT_STASHED" \
   101‚Üí        -v CLEAN="$ZSH_THEME_GIT_PROMPT_CLEAN" \
   102‚Üí        -v RC="%{$reset_color%}" \
   103‚Üí        '
   104‚Üí            BEGIN {
   105‚Üí                ORS = "";
   106‚Üí
   107‚Üí                fatal = 0;
   108‚Üí                oid = "";
   109‚Üí                head = "";
   110‚Üí                upstream = "";
   111‚Üí                ahead = 0;
   112‚Üí                behind = 0;
   113‚Üí                untracked = 0;
   114‚Üí                unmerged = 0;
   115‚Üí                staged = 0;
   116‚Üí                unstaged = 0;
   117‚Üí                stashed = 0;
   118‚Üí            }
   119‚Üí
   120‚Üí            $1 == "fatal:" {
   121‚Üí                fatal = 1;
   122‚Üí            }
   123‚Üí
   124‚Üí            $2 == "branch.oid" {
   125‚Üí                oid = $3;
   126‚Üí            }
   127‚Üí
   128‚Üí            $2 == "branch.head" {
   129‚Üí                head = $3;
   130‚Üí            }
   131‚Üí
   132‚Üí            $2 == "branch.upstream" {
   133‚Üí                upstream = $3;
   134‚Üí            }
   135‚Üí
   136‚Üí            $2 == "branch.ab" {
   137‚Üí                ahead = $3;
   138‚Üí                behind = $4;
   139‚Üí            }
   140‚Üí
   141‚Üí            $1 == "?" {
   142‚Üí                ++untracked;
   143‚Üí            }
   144‚Üí
   145‚Üí            $1 == "u" {
   146‚Üí                ++unmerged;
   147‚Üí            }
   148‚Üí
   149‚Üí            $1 == "1" || $1 == "2" {
   150‚Üí                split($2, arr, "");
   151‚Üí                if (arr[1] != ".") {
   152‚Üí                    ++staged;
   153‚Üí                }
   154‚Üí                if (arr[2] != ".") {
   155‚Üí                    ++unstaged;
   156‚Üí                }
   157‚Üí            }
   158‚Üí
   159‚Üí            $2 == "stash.count" {
   160‚Üí                stashed = $3;
   161‚Üí            }
   162‚Üí
   163‚Üí            END {
   164‚Üí                if (fatal == 1) {
   165‚Üí                    exit(1);
   166‚Üí                }
   167‚Üí
   168‚Üí                print PREFIX;
   169‚Üí                print RC;
   170‚Üí
   171‚Üí                if (head == "(detached)") {
   172‚Üí                    print DETACHED;
   173‚Üí                    print substr(oid, 0, 7);
   174‚Üí                } else {
   175‚Üí                    print BRANCH;
   176‚Üí                    gsub("%", "%%", head);
   177‚Üí                    print head;
   178‚Üí                }
   179‚Üí                print RC;
   180‚Üí
   181‚Üí                if (upstream == "") {
   182‚Üí                    print UPSTREAM_NO_TRACKING;
   183‚Üí                } else if (UPSTREAM_TYPE == "symbol") {
   184‚Üí                    print UPSTREAM_SYMBOL;
   185‚Üí                } else if (UPSTREAM_TYPE == "full") {
   186‚Üí                    print UPSTREAM_PREFIX;
   187‚Üí                    gsub("%", "%%", upstream);
   188‚Üí                    print upstream;
   189‚Üí                    print UPSTREAM_SUFFIX;
   190‚Üí                }
   191‚Üí
   192‚Üí                print RC;
   193‚Üí
   194‚Üí                if (behind < 0) {
   195‚Üí                    print BEHIND;
   196‚Üí                    printf "%d", behind * -1;
   197‚Üí                    print RC;
   198‚Üí                }
   199‚Üí
   200‚Üí                if (ahead > 0) {
   201‚Üí                    print AHEAD;
   202‚Üí                    printf "%d", ahead;
   203‚Üí                    print RC;
   204‚Üí                }
   205‚Üí
   206‚Üí                print SEPARATOR;
   207‚Üí
   208‚Üí                if (unmerged > 0) {
   209‚Üí                    print UNMERGED;
   210‚Üí                    print unmerged;
   211‚Üí                    print RC;
   212‚Üí                }
   213‚Üí
   214‚Üí                if (staged > 0) {
   215‚Üí                    print STAGED;
   216‚Üí                    print staged;
   217‚Üí                    print RC;
   218‚Üí                }
   219‚Üí
   220‚Üí                if (unstaged > 0) {
   221‚Üí                    print UNSTAGED;
   222‚Üí                    print unstaged;
   223‚Üí                    print RC;
   224‚Üí                }
   225‚Üí
   226‚Üí                if (untracked > 0) {
   227‚Üí                    print UNTRACKED;
   228‚Üí                    print untracked;
   229‚Üí                    print RC;
   230‚Üí                }
   231‚Üí
   232‚Üí                if (stashed > 0) {
   233‚Üí                    print STASHED;
   234‚Üí                    print stashed;
   235‚Üí                    print RC;
   236‚Üí                }
   237‚Üí
   238‚Üí                if (unmerged == 0 && staged == 0 && unstaged == 0 && untracked == 0) {
   239‚Üí                    print CLEAN;
   240‚Üí                    print RC;
   241‚Üí                }
   242‚Üí
   243‚Üí                print SUFFIX;
   244‚Üí                print RC;
   245‚Üí            }
   246‚Üí        '
   247‚Üí}
   248‚Üí
   249‚Üífunction _zsh_git_prompt_git_status_secondary() {
   250‚Üí    tags=$(command git tag --points-at=HEAD 2> /dev/null)
   251‚Üí
   252‚Üí    [[ -z "$tags" ]] && return
   253‚Üí
   254‚Üí    echo -n ${ZSH_THEME_GIT_PROMPT_SECONDARY_PREFIX}
   255‚Üí    echo -n ${ZSH_THEME_GIT_PROMPT_TAGS_PREFIX}
   256‚Üí
   257‚Üí    echo "$tags" | $ZSH_GIT_PROMPT_AWK_CMD \
   258‚Üí        -v SEPARATOR="$ZSH_THEME_GIT_PROMPT_TAGS_SEPARATOR" \
   259‚Üí        -v TAG="$ZSH_THEME_GIT_PROMPT_TAG" \
   260‚Üí        -v RC="%{$reset_color%}" \
   261‚Üí        '
   262‚Üí            BEGIN {
   263‚Üí                ORS = "";
   264‚Üí            }
   265‚Üí            {
   266‚Üí                if (NR != 1) {
   267‚Üí                    print SEPARATOR;
   268‚Üí                    print RC;
   269‚Üí                }
   270‚Üí                print TAG;
   271‚Üí                print $0;
   272‚Üí                print RC;
   273‚Üí            }
   274‚Üí        '
   275‚Üí
   276‚Üí    echo -n ${ZSH_THEME_GIT_PROMPT_TAGS_SUFFIX}
   277‚Üí    echo -n ${ZSH_THEME_GIT_PROMPT_SECONDARY_SUFFIX}
   278‚Üí}
   279‚Üí
   280‚Üí
   281‚Üí# The async code is taken from
   282‚Üí# https://github.com/zsh-users/zsh-autosuggestions/blob/master/src/async.zsh
   283‚Üí
   284‚Üízmodload zsh/system
   285‚Üí
   286‚Üífunction _zsh_git_prompt_async_request() {
   287‚Üí    typeset -g _ZSH_GIT_PROMPT_ASYNC_FD _ZSH_GIT_PROMPT_ASYNC_PID
   288‚Üí
   289‚Üí    # If we've got a pending request, cancel it
   290‚Üí    if [[ -n "$_ZSH_GIT_PROMPT_ASYNC_FD" ]] && { true <&$_ZSH_GIT_PROMPT_ASYNC_FD } 2>/dev/null;
   291‚Üí    then
   292‚Üí
   293‚Üí        # Close the file descriptor and remove the handler
   294‚Üí        exec {_ZSH_GIT_PROMPT_ASYNC_FD}<&-
   295‚Üí        zle -F $_ZSH_GIT_PROMPT_ASYNC_FD
   296‚Üí
   297‚Üí        # Zsh will make a new process group for the child process only if job
   298‚Üí        # control is enabled (MONITOR option)
   299‚Üí        if [[ -o MONITOR ]]; then
   300‚Üí            # Send the signal to the process group to kill any processes that may
   301‚Üí            # have been forked by the suggestion strategy
   302‚Üí            kill -TERM -$_ZSH_GIT_PROMPT_ASYNC_PID 2>/dev/null
   303‚Üí        else
   304‚Üí            # Kill just the child process since it wasn't placed in a new process
   305‚Üí            # group. If the suggestion strategy forked any child processes they may
   306‚Üí            # be orphaned and left behind.
   307‚Üí            kill -TERM $_ZSH_GIT_PROMPT_ASYNC_PID 2>/dev/null
   308‚Üí        fi
   309‚Üí    fi
   310‚Üí
   311‚Üí    # Fork a process to fetch the git status and open a pipe to read from it
   312‚Üí    exec {_ZSH_GIT_PROMPT_ASYNC_FD}< <(
   313‚Üí        # Tell parent process our pid
   314‚Üí        builtin echo $sysparams[pid]
   315‚Üí
   316‚Üí        _zsh_git_prompt_git_status
   317‚Üí        [[ -n "$ZSH_GIT_PROMPT_ENABLE_SECONDARY" ]] \
   318‚Üí            && builtin echo -n "##secondary##" \
   319‚Üí            && _zsh_git_prompt_git_status_secondary
   320‚Üí    )
   321‚Üí
   322‚Üí    # There's a weird bug here where ^C stops working unless we force a fork
   323‚Üí    # See https://github.com/zsh-users/zsh-autosuggestions/issues/364
   324‚Üí    command true
   325‚Üí
   326‚Üí    # Read the pid from the child process
   327‚Üí    read _ZSH_GIT_PROMPT_ASYNC_PID <&$_ZSH_GIT_PROMPT_ASYNC_FD
   328‚Üí
   329‚Üí    # When the fd is readable, call the response handler
   330‚Üí    zle -F "$_ZSH_GIT_PROMPT_ASYNC_FD" _zsh_git_prompt_callback
   331‚Üí}
   332‚Üí
   333‚Üí# Called when new data is ready to be read from the pipe
   334‚Üí# First arg will be fd ready for reading
   335‚Üí# Second arg will be passed in case of error
   336‚Üí_ZSH_GIT_PROMPT_STATUS_OUTPUT=""
   337‚Üí_ZSH_GIT_PROMPT_STATUS_SECONDARY_OUTPUT=""
   338‚Üífunction _zsh_git_prompt_callback() {
   339‚Üí    emulate -L zsh
   340‚Üí    local old_primary="$_ZSH_GIT_PROMPT_STATUS_OUTPUT"
   341‚Üí    local old_secondary="$_ZSH_GIT_PROMPT_STATUS_SECONDARY_OUTPUT"
   342‚Üí    local fd_data
   343‚Üí    local -a output
   344‚Üí
   345‚Üí    if [[ -z "$2" || "$2" == "hup" ]]; then
   346‚Üí        # Read output from fd
   347‚Üí        fd_data="$(cat <&$1)"
   348‚Üí        output=( ${(s:##secondary##:)fd_data} )
   349‚Üí        _ZSH_GIT_PROMPT_STATUS_OUTPUT="${output[1]}"
   350‚Üí        _ZSH_GIT_PROMPT_STATUS_SECONDARY_OUTPUT="${output[2]}"
   351‚Üí
   352‚Üí        if [[ "$old_primary" != "$_ZSH_GIT_PROMPT_STATUS_OUTPUT" ]] \
   353‚Üí            || [[ "$old_secondary" != "$_ZSH_GIT_PROMPT_STATUS_SECONDARY_OUTPUT" ]] ; then
   354‚Üí            zle reset-prompt
   355‚Üí            zle -R
   356‚Üí        fi
   357‚Üí
   358‚Üí        # Close the fd
   359‚Üí        exec {1}<&-
   360‚Üí    fi
   361‚Üí
   362‚Üí    # Always remove the handler
   363‚Üí    zle -F "$1"
   364‚Üí
   365‚Üí    # Unset global FD variable to prevent closing user created FDs in the precmd hook
   366‚Üí    unset _ZSH_GIT_PROMPT_ASYNC_FD
   367‚Üí}
   368‚Üí
   369‚Üífunction _zsh_git_prompt_precmd_hook() {
   370‚Üí    if [[ -n "$ZSH_GIT_PROMPT_FORCE_BLANK" ]]; then
   371‚Üí        _ZSH_GIT_PROMPT_STATUS_OUTPUT=""
   372‚Üí        _ZSH_GIT_PROMPT_STATUS_SECONDARY_OUTPUT=""
   373‚Üí    fi
   374‚Üí    _zsh_git_prompt_async_request
   375‚Üí}
   376‚Üí
   377‚Üíif (( $+commands[git] )); then
   378‚Üí    if [[ -z "$ZSH_GIT_PROMPT_NO_ASYNC" ]]; then
   379‚Üí        autoload -U add-zsh-hook \
   380‚Üí            && add-zsh-hook precmd _zsh_git_prompt_precmd_hook
   381‚Üí
   382‚Üí        function gitprompt() {
   383‚Üí            echo -n "$_ZSH_GIT_PROMPT_STATUS_OUTPUT"
   384‚Üí        }
   385‚Üí
   386‚Üí        function gitprompt_secondary() {
   387‚Üí            echo -n "$_ZSH_GIT_PROMPT_STATUS_SECONDARY_OUTPUT"
   388‚Üí        }
   389‚Üí    else
   390‚Üí        function gitprompt() {
   391‚Üí            _zsh_git_prompt_git_status
   392‚Üí        }
   393‚Üí
   394‚Üí        function gitprompt_secondary() {
   395‚Üí            [[ -n "$ZSH_GIT_PROMPT_ENABLE_SECONDARY" ]] \
   396‚Üí                && _zsh_git_prompt_git_status_secondary
   397‚Üí        }
   398‚Üí    fi
   399‚Üíelse
   400‚Üí    function gitprompt() { }
   401‚Üí    function gitprompt_secondary() { }
   402‚Üífi
   403‚Üí

